import React, { Component } from 'react';
import './styles/AvatarList.css';

const STARTING_REPO = 999;

class User {
  constructor(username, id, avatarURL, followersURL) {
    this.username = username;
    this.id = id;
    this.avatarURL = avatarURL;
    this.followersURL = followersURL;

    /* Set explicitly to 'undefined' because we don't know whether they do or don't exist
     * for this user yet. */
    this.followers = undefined;
  }
}

class AvatarList extends Component {
  constructor(props) {
    super(props);

    this.state = {
      'users': [],
    };
  }

  componentDidMount() {
    fetch(`http://localhost:3000/repos?since=${STARTING_REPO}`)
      .then((response) => {
        return response.json();
      })
      .then((response) => {
        console.log(response);
        let newUsers = JSON.parse(JSON.stringify(response)).map((value) => {
          return new User(value.username, value.id, value.avatarURL, value.followers);
        });

        this.setState({
          'users': newUsers,
        });
      }) // .bind(this))
      .catch((error) => {
        console.log(error);
        return Promise.reject(error);
      });
  }

  render() {
    const avatars = this.state.users.map((value) => {
      return (
        <li key={value.id}>
          <img src={value.avatarURL} alt='Avatar' />
          <p>{value.id}</p>
        </li>
      );
    });
    return (
      <div className='avatarList'>
        <p>AvatarList</p>
        <ul>
          {avatars}
        </ul>
      </div>
    );
  }
}

export default AvatarList;
