import React, { Component } from 'react';
import PropTypes from 'prop-types';
import jQuery from 'jquery';
import Popper from 'popper.js';
import BaseURLs from './BaseURLs';
import 'bootstrap/dist/css/bootstrap.min.css';

class Avatar extends Component {
  constructor(props) {
    super(props);

    this.state = {
      'followers': [],
    };

    this.displayFollowersPopover = this.displayFollowersPopover.bind(this);
  }

  componentDidMount() {
    jQuery('[data-toggle="popover"]').popover();
  }

  displayFollowersPopover(e) {
    // jQuery(e.target).popover('show');
    // console.log(e.target.getAttribute('data-content'));
    e.persist();
    let temp = e.target;

    // console.log(this.props.login);
    fetch(`${BaseURLs.followers}${this.props.login}`)
      .then((response) => {
        return response.json();
      })
      .then((response) => {
        console.log(JSON.parse(JSON.stringify(response)));

        this.setState({
          'followers': response,
        }, () => {
          jQuery(temp).popover('show');
        });
      });
  }

  render() {
    // Bootstrap's data-content attribute will only accept HTML as a string
    let followerDisplayString = '';
    this.state.followers.forEach((value) => {
      followerDisplayString += `<li>${value.login}</li>`;
    });
    console.log('Follower display', followerDisplayString);
    let classes = [];
    let handleMouseEnter;

    if (this.props.login.substring(0, 1) === 'a') {
      classes.push('avatar__img--highlighted');
      handleMouseEnter = this.displayFollowersPopover;
    }

    return (
      <div>
        <img
          className={`img-fluid ${classes.join(' ')}`}
          src={this.props.avatarURL}
          alt={`${this.props.login}'s avatar`}
          data-html='true'
          onMouseEnter={handleMouseEnter}
          data-toggle='popover'
          data-trigger='none'
          title={this.props.login}
          data-content={`<ul>${followerDisplayString}</ul>`}
        />
      </div>
    );
  }
}

Avatar.propTypes = {
  'avatarURL': PropTypes.string.isRequired,
  'login': PropTypes.string.isRequired,
};

export default Avatar;
