import React, { Component } from 'react';
import PropTypes from 'prop-types';
import 'bootstrap/dist/css/bootstrap.min.css';

class Avatar extends Component {
  displayFollowersPopover() {
    console.log(this.props.followersURL);
  }

  render() {
    let classes = [];
    let handleMouseEnter;

    if (this.props.login.substring(0, 1) === 'a') {
      classes.push('avatar__img--highlighted');
      handleMouseEnter = this.displayFollowersPopover;
    }

    return (
      <img className={`img-fluid ${classes.join(' ')}`} src={this.props.avatarURL} alt={`${this.props.login}'s avatar`} onMouseEnter={handleMouseEnter} />
    );
  }
}

Avatar.propTypes = {
  'avatarURL': PropTypes.string.isRequired,
  'login': PropTypes.string.isRequired,
  'followersURL': PropTypes.string.isRequired,
};

